import pbp2 from './assets/pbp2.png';

export function Skills() {
  const skillCategories = [
    {
      icon: pbp2,
      title: "Google Pixel Buds Pro 2",
      skills: ["Injection Molding", "Sensors", "Cross Functional Collaboration"],
      description: "2nd Generation Google Pixel Buds - 27% Smaller and Lighter",
      link: "https://store.google.com/product/pixel_buds_pro_2?hl=en-US",
      linktext: "Official Google Store"
    },
    {
      icon: pbp2,
      title: "Google Pixel Buds Pro",
      skills: ["Node.js", "Python", "PostgreSQL", "MongoDB", "Express.js"],
      description: "1st Generation Google Pixel Buds Pro Case",
      link: "https://store.google.com/product/pixel_buds_pro_2?hl=en-US",
      linktext: "Official Google Store"
    }
  ];

  return (
    <section id="skills" className="py-16 bg-slate-50">
      <div className="container mx-auto px-6 lg:px-8">
        <div className="max-w-6xl mx-auto">
          <div className="text-center mb-12">
            <h2 className="text-3xl lg:text-4xl mb-4">Products</h2>
            <p className="text-muted-foreground max-w-2xl mx-auto">
              Products I've helped bring to life
            </p>
          </div>

          <div className="grid sm:grid-cols-1 lg:grid-cols-1 gap-6">
            {skillCategories.map((category, index) => {
              const IconComponent = category.icon;
              return (
                <div key={index} className="bg-white border rounded-xl p-6">
                  <div className="flex items-center mb-4">
                    <div className="w-10 h-10 bg-slate-100 rounded-lg flex items-center justify-center mr-3">
                      {/*<IconComponent className="text-foreground" />*/}
                    </div>
                    <h3>{category.title}</h3>
                  </div>

                  <p className="text-sm text-muted-foreground mb-4">
                    {category.description}
                  </p>

                  <p className="text-sm text-muted-foreground mb-4">
                    <a href={category.link}>{category.linktext}</a>
                  </p>

                  <div className="flex flex-wrap gap-2">
                    {category.skills.map((skill, skillIndex) => (
                      <span
                        key={skillIndex}
                        className="px-2 py-1 bg-slate-200 text-sm rounded"
                      >
                        {skill}
                      </span>
                    ))}
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      </div>
    </section>
  );
}
